import React, { Component } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items";

class App extends React.Component {

constructor(props){
  super(props)
  this.state= {
    orders: [],
    items: [
      {
        id: 1,
        title: 'grey chair',
        img: 'chair-grey.jpeg',
        desc: 'Stylish and comfortable grey upholstered chair',
        category: 'chairs',
        price: 49.99
      },
      {
        id: 2,
        title: 'wooden table',
        img: 'table-wood.jpeg',
        desc: 'Elegant wooden dining table',
        category: 'tables',
        price: 129.99
      },
      {
        id: 3,
        title: 'modern lamp',
        img: 'lamp-modern.jpeg',
        desc: 'Stylish modern floor lamp',
        category: 'lighting',
        price: 39.99
      },
      {
        id: 4,
        title: 'blue sofa',
        img: 'sofa-blue.jpeg',
        desc: 'Comfortable blue fabric sofa',
        category: 'sofas',
        price: 299.99
      },
      {
        id: 5,
        title: 'bookshelf',
        img: 'bookshelf.jpeg',
        desc: 'Spacious wooden bookshelf',
        category: 'storage',
        price: 89.99
      },
      {
        id: 6,
        title: 'wall clock',
        img: 'clock-wall.jpeg',
        desc: 'Minimalist wall clock',
        category: 'decor',
        price: 24.99
      },
      {
        id: 7,
        title: 'area rug',
        img: 'rug-area.jpeg',
        desc: 'Soft patterned area rug',
        category: 'rugs',
        price: 59.99
      },
      {
        id: 8,
        title: 'kitchen stool',
        img: 'stool-kitchen.jpeg',
        desc: 'Compact kitchen counter stool',
        category: 'stools',
        price: 34.99
      },
      {
        id: 9,
        title: 'bedside table',
        img: 'table-bedside.jpeg',
        desc: 'Small nightstand with drawer',
        category: 'bedroom',
        price: 44.99
      },
      {
        id: 10,
        title: 'floor mirror',
        img: 'mirror-floor.jpeg',
        desc: 'Full-length floor mirror',
        category: 'mirrors',
        price: 74.99
      }    
    ]
  }
  this.addToOrder= this.addToOrder.bind(this)
}

  render(){
    return (
      <div className='wrapper'>
        <Header orders={this.state.orders}/>
        <Items items={this.state.items} onAdd={this.addToOrder} />
        <Footer/>
      </div>
    );
  }

  addToOrder (item) {
    this.setState({orders:[...this.state.orders, item]}
    )
  }

}

export default App;
